import React, { useState } from 'react';
import { useAuth } from '../context/AuthContext';
import { FiLogOut, FiX } from 'react-icons/fi';

const LogoutButton = ({ className = '', style = {} }) => {
  const [showConfirm, setShowConfirm] = useState(false);
  const { logout, user } = useAuth();

  const handleLogout = () => {
    logout();
    setShowConfirm(false);
  };

  const handleCancel = () => {
    setShowConfirm(false);
  };

  return (
    <>
      <button 
        onClick={() => setShowConfirm(true)} 
        style={style} 
        className={className}
      >
        <FiLogOut size={16} />
        <span>Logout</span>
      </button>

      {showConfirm && (
        <div className="logout-modal-backdrop" onClick={handleCancel}>
          <div className="logout-modal" onClick={(e) => e.stopPropagation()}>
            <div className="logout-modal-header">
              <h3>Confirm Logout</h3>
              <button onClick={handleCancel} className="close-button">
                <FiX size={20} />
              </button>
            </div>
            <div className="logout-modal-body">
              <p>Are you sure you want to logout, <strong>{user?.firstName}</strong>?</p>
              <p>You'll need to sign in again to access your account.</p>
            </div>
            <div className="logout-modal-footer">
              <button onClick={handleCancel} className="cancel-button">
                Cancel
              </button>
              <button onClick={handleLogout} className="confirm-logout-button">
                <FiLogOut size={16} />
                Yes, Logout
              </button>
            </div>
          </div>
        </div>
      )}

      <style jsx>{`
        .logout-modal-backdrop {
          position: fixed;
          top: 0;
          left: 0;
          right: 0;
          bottom: 0;
          background: rgba(0, 0, 0, 0.5);
          display: flex;
          align-items: center;
          justify-content: center;
          z-index: 2000;
          animation: fadeIn 0.2s ease-out;
        }

        @keyframes fadeIn {
          from { opacity: 0; }
          to { opacity: 1; }
        }

        .logout-modal {
          background: white;
          border-radius: 12px;
          width: 90%;
          max-width: 400px;
          box-shadow: 0 20px 40px rgba(0, 0, 0, 0.15);
          animation: slideUp 0.3s ease-out;
        }

        @keyframes slideUp {
          from {
            opacity: 0;
            transform: translateY(30px);
          }
          to {
            opacity: 1;
            transform: translateY(0);
          }
        }

        .logout-modal-header {
          padding: 20px 20px 0 20px;
          display: flex;
          align-items: center;
          justify-content: space-between;
        }

        .logout-modal-header h3 {
          margin: 0;
          color: #333;
          font-size: 18px;
          font-weight: 600;
        }

        .close-button {
          background: none;
          border: none;
          color: #666;
          cursor: pointer;
          padding: 4px;
          border-radius: 4px;
          transition: all 0.2s ease;
        }

        .close-button:hover {
          background: #f5f5f5;
          color: #333;
        }

        .logout-modal-body {
          padding: 20px;
        }

        .logout-modal-body p {
          margin: 0 0 12px 0;
          color: #666;
          line-height: 1.5;
        }

        .logout-modal-body p:last-child {
          margin-bottom: 0;
        }

        .logout-modal-footer {
          padding: 0 20px 20px 20px;
          display: flex;
          gap: 12px;
          justify-content: flex-end;
        }

        .cancel-button {
          background: #f8f9fa;
          border: 1px solid #dee2e6;
          color: #6c757d;
          padding: 8px 16px;
          border-radius: 6px;
          cursor: pointer;
          font-weight: 500;
          transition: all 0.2s ease;
        }

        .cancel-button:hover {
          background: #e9ecef;
          border-color: #adb5bd;
        }

        .confirm-logout-button {
          background: #dc3545;
          border: 1px solid #dc3545;
          color: white;
          padding: 8px 16px;
          border-radius: 6px;
          cursor: pointer;
          font-weight: 500;
          display: flex;
          align-items: center;
          gap: 6px;
          transition: all 0.2s ease;
        }

        .confirm-logout-button:hover {
          background: #c82333;
          border-color: #bd2130;
          transform: translateY(-1px);
        }

        @media (max-width: 480px) {
          .logout-modal {
            margin: 20px;
          }
          
          .logout-modal-footer {
            flex-direction: column-reverse;
          }
          
          .cancel-button,
          .confirm-logout-button {
            width: 100%;
            justify-content: center;
          }
        }
      `}</style>
    </>
  );
};

export default LogoutButton;
