import React, { useState, useEffect } from 'react';
import { ordersAPI, productsAPI } from '../services/api';
import { toast } from 'react-toastify';
import { FiPlus, FiEye, FiX, FiShoppingCart } from 'react-icons/fi';

const Orders = () => {
  const [orders, setOrders] = useState([]);
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showModal, setShowModal] = useState(false);
  const [viewOrder, setViewOrder] = useState(null);
  const [formData, setFormData] = useState({
    customerName: '',
    customerPhone: '',
    items: [{ productId: '', quantity: 1, customPrice: 0 }],
  });

  useEffect(() => {
    fetchOrders();
    fetchProducts();
  }, []);

  const fetchOrders = async () => {
    try {
      setLoading(true);
      const response = await ordersAPI.getAll();
      setOrders(response.data);
    } catch (error) {
      toast.error('Failed to fetch orders');
      console.error(error);
    } finally {
      setLoading(false);
    }
  };

  const fetchProducts = async () => {
    try {
      const response = await productsAPI.getAll({ isActive: true });
      setProducts(response.data);
    } catch (error) {
      console.error('Failed to fetch products:', error);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await ordersAPI.create(formData);
      toast.success('Order created successfully');
      fetchOrders();
      closeModal();
    } catch (error) {
      toast.error(error.response?.data?.message || 'Failed to create order');
    }
  };

  const handleStatusUpdate = async (orderId, status, paymentStatus) => {
    try {
      await ordersAPI.updateStatus(orderId, { status, paymentStatus });
      toast.success('Order status updated');
      fetchOrders();
    } catch (error) {
      toast.error('Failed to update order status');
    }
  };

  const openModal = () => {
    setFormData({
      customerName: '',
      customerPhone: '',
      items: [{ productId: '', quantity: 1, customPrice: 0 }],
    });
    setShowModal(true);
  };

  const closeModal = () => {
    setShowModal(false);
    setViewOrder(null);
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleItemChange = (index, field, value) => {
    const newItems = [...formData.items];
    newItems[index][field] = value;
    setFormData(prev => ({
      ...prev,
      items: newItems
    }));
  };

  const addItem = () => {
    setFormData(prev => ({
      ...prev,
      items: [...prev.items, { productId: '', quantity: 1 }]
    }));
  };

  const removeItem = (index) => {
    if (formData.items.length > 1) {
      const newItems = formData.items.filter((_, i) => i !== index);
      setFormData(prev => ({
        ...prev,
        items: newItems
      }));
    }
  };

  const calculateTotal = () => {
    return formData.items.reduce((sum, item) => {
      const product = products.find(p => p._id === item.productId);
      return sum + (product ? product.sellingPrice * item.quantity : 0);
    }, 0);
  };

  const formatCurrency = (amount) => {
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: 'BDT',
    }).format(amount || 0);
  };

  const getStatusBadge = (status) => {
    const statusColors = {
      pending: 'badge-warning',
      processing: 'badge-primary',
      shipped: 'badge-secondary',
      delivered: 'badge-success',
      cancelled: 'badge-danger',
    };
    return `badge ${statusColors[status] || 'badge-secondary'}`;
  };

  const getPaymentStatusBadge = (status) => {
    const statusColors = {
      pending: 'badge-warning',
      paid: 'badge-success',
      failed: 'badge-danger',
      refunded: 'badge-secondary',
    };
    return `badge ${statusColors[status] || 'badge-secondary'}`;
  };

  if (loading) {
    return <div className="container"><div className="loading">Loading orders...</div></div>;
  }

  return (
    <div className="container" style={{ padding: '20px' }}>
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '30px' }}>
        <h1>Orders</h1>
        <button className="btn btn-primary" onClick={openModal}>
          <FiPlus style={{ marginRight: '8px' }} />
          Create Order
        </button>
      </div>

      {orders.length === 0 ? (
        <div className="card text-center" style={{ padding: '40px' }}>
          <FiShoppingCart size={60} color="#6c757d" style={{ marginBottom: '20px' }} />
          <h3>No orders found</h3>
          <p>Start by creating your first order.</p>
          <button className="btn btn-primary" onClick={openModal}>
            Create Your First Order
          </button>
        </div>
      ) : (
        <div className="card">
          <div className="table-responsive">
            <table className="table">
              <thead>
                <tr>
                  <th>Order #</th>
                  <th>Customer</th>
                  <th>Items</th>
                  <th>Total</th>
                  <th>Profit</th>
                  <th>Status</th>
                  <th>Payment</th>
                  <th>Date</th>
                  <th>Actions</th>
                </tr>
              </thead>
              <tbody>
                {orders.map((order) => (
                  <tr key={order._id}>
                    <td style={{ fontWeight: '600' }}>{order.orderNumber}</td>
                    <td>
                      <div>
                        <div style={{ fontWeight: '500' }}>{order.customerName}</div>
                        <div style={{ fontSize: '12px', color: '#6c757d' }}>
                          {order.customerEmail}
                        </div>
                      </div>
                    </td>
                    <td>{order.items.length} items</td>
                    <td>{formatCurrency(order.total)}</td>
                    <td>
                      <span style={{ color: order.total - order.totalCost > 0 ? '#28a745' : '#dc3545' }}>
                        {formatCurrency(order.total - order.totalCost)}
                      </span>
                    </td>
                    <td>
                      <select
                        className={getStatusBadge(order.status)}
                        value={order.status}
                        onChange={(e) => handleStatusUpdate(order._id, e.target.value, order.paymentStatus)}
                        style={{ border: 'none', background: 'transparent', color: 'inherit' }}
                      >
                        <option value="pending">Pending</option>
                        <option value="processing">Processing</option>
                        <option value="shipped">Shipped</option>
                        <option value="delivered">Delivered</option>
                        <option value="cancelled">Cancelled</option>
                      </select>
                    </td>
                    <td>
                      <select
                        className={getPaymentStatusBadge(order.paymentStatus)}
                        value={order.paymentStatus}
                        onChange={(e) => handleStatusUpdate(order._id, order.status, e.target.value)}
                        style={{ border: 'none', background: 'transparent', color: 'inherit' }}
                      >
                        <option value="pending">Pending</option>
                        <option value="paid">Paid</option>
                        <option value="failed">Failed</option>
                        <option value="refunded">Refunded</option>
                      </select>
                    </td>
                    <td>{new Date(order.createdAt).toLocaleDateString()}</td>
                    <td>
                      <button
                        className="btn btn-sm btn-primary"
                        onClick={() => setViewOrder(order)}
                      >
                        <FiEye size={14} />
                      </button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      )}

      {/* Create Order Modal */}
      {showModal && (
        <div style={modalStyles.overlay}>
          <div style={modalStyles.modal}>
            <div style={modalStyles.header}>
              <h3>Create New Order</h3>
              <button onClick={closeModal} style={modalStyles.closeButton}></button>
            </div>
            <form onSubmit={handleSubmit}>
              <div style={{ padding: '20px' }}>
                <div className="row">
                  <div className="col-md-6">
                    <div className="form-group">
                      <label className="form-label">Customer Name</label>
                      <input
                        type="text"
                        name="customerName"
                        value={formData.customerName}
                        onChange={handleInputChange}
                        className="form-control"
                        required
                      />
                    </div>
                  </div>
                  <div className="col-md-6">
                    <div className="form-group">
                      <label className="form-label">Phone</label>
                      <input
                        type="tel"
                        name="customerPhone"
                        value={formData.customerPhone}
                        onChange={handleInputChange}
                        className="form-control"
                        required
                      />
                    </div>
                  </div>
                </div>

                <h4>Order Items</h4>
                {formData.items.map((item, index) => (
                  <div key={index} className="card" style={{ marginBottom: '15px', padding: '15px' }}>
                    <div className="row">
                      <div className="col-md-6">
                        <label className="form-label">Product</label>
                        <select
                          value={item.productId}
                          onChange={(e) => handleItemChange(index, 'productId', e.target.value)}
                          className="form-control"
                          required
                        >
                          <option value="">Select Product</option>
                          {products.map(product => (
                            <option key={product._id} value={product._id}>
                              {product.name} - {formatCurrency(product.sellingPrice)} (Stock: {product.stock})
                            </option>
                          ))}
                        </select>
                      </div>
                      <div className="col-md-4">
                        <label className="form-label">Quantity</label>
                        <input
                          type="number"
                          value={item.quantity}
                          onChange={(e) => handleItemChange(index, 'quantity', parseInt(e.target.value) || 1)}
                          className="form-control"
                          min="1"
                          required
                        />
                      </div>
                      <div className="col-md-2">
                        <label className="form-label">&nbsp;</label>
                        <button
                          type="button"
                          className="btn btn-danger btn-sm"
                          onClick={() => removeItem(index)}
                          disabled={formData.items.length === 1}
                          style={{ width: '100%' }}
                        >
                          <FiX />
                        </button>
                      </div>
                    </div>
                  </div>
                ))}

                <button type="button" className="btn btn-secondary mb-3" onClick={addItem}>
                  Add Item
                </button>

                <div className="card" style={{ backgroundColor: '#f8f9fa', marginTop: '20px' }}>
                  <div style={{ padding: '15px' }}>
                    <h4>Total Bill</h4>
                    <p style={{ fontSize: '24px', fontWeight: 'bold', color: '#007bff', margin: '10px 0' }}>
                      {formatCurrency(calculateTotal())}
                    </p>
                  </div>
                </div>
              </div>

              <div style={modalStyles.footer}>
                <button type="button" className="btn btn-secondary mr-2" onClick={closeModal}>
                  Cancel
                </button>
                <button type="submit" className="btn btn-primary">
                  Create Order
                </button>
              </div>
            </form>
          </div>
        </div>
      )}

      {/* View Order Modal */}
      {viewOrder && (
        <div style={modalStyles.overlay}>
          <div style={modalStyles.modal}>
            <div style={modalStyles.header}>
              <h3>Order Details - {viewOrder.orderNumber}</h3>
              <button onClick={closeModal} style={modalStyles.closeButton}></button>
            </div>
            <div style={{ padding: '20px' }}>
              <div className="row">
                <div className="col-md-6">
                  <h4>Customer Information</h4>
                  <p><strong>Name:</strong> {viewOrder.customerName}</p>
                  <p><strong>Email:</strong> {viewOrder.customerEmail}</p>
                  <p><strong>Phone:</strong> {viewOrder.customerPhone}</p>
                </div>
                <div className="col-md-6">
                  <h4>Order Information</h4>
                  <p><strong>Status:</strong> <span className={getStatusBadge(viewOrder.status)}>{viewOrder.status}</span></p>
                  <p><strong>Payment:</strong> <span className={getPaymentStatusBadge(viewOrder.paymentStatus)}>{viewOrder.paymentStatus}</span></p>
                  <p><strong>Date:</strong> {new Date(viewOrder.createdAt).toLocaleDateString()}</p>
                </div>
              </div>
              
              <h4>Order Items</h4>
              <table className="table">
                <thead>
                  <tr>
                    <th>Product</th>
                    <th>Quantity</th>
                    <th>Price</th>
                    <th>Total</th>
                  </tr>
                </thead>
                <tbody>
                  {viewOrder.items.map((item, index) => (
                    <tr key={index}>
                      <td>{item.product?.name || 'Unknown Product'}</td>
                      <td>{item.quantity}</td>
                      <td>{formatCurrency(item.priceAtTime)}</td>
                      <td>{formatCurrency(item.priceAtTime * item.quantity)}</td>
                    </tr>
                  ))}
                </tbody>
              </table>

              <div className="row">
                <div className="col-md-6">
                  {viewOrder.notes && (
                    <>
                      <h4>Notes</h4>
                      <p>{viewOrder.notes}</p>
                    </>
                  )}
                </div>
                <div className="col-md-6">
                  <div className="card">
                    <div style={{ padding: '15px' }}>
                      <h4>Order Summary</h4>
                      <p>Subtotal: <strong>{formatCurrency(viewOrder.subtotal)}</strong></p>
                      <p>Tax: <strong>{formatCurrency(viewOrder.tax)}</strong></p>
                      <p>Shipping: <strong>{formatCurrency(viewOrder.shipping)}</strong></p>
                      <p>Discount: <strong>-{formatCurrency(viewOrder.discount)}</strong></p>
                      <hr />
                      <p>Total: <strong>{formatCurrency(viewOrder.total)}</strong></p>
                      <p>Profit: <strong style={{ color: viewOrder.total - viewOrder.totalCost > 0 ? '#28a745' : '#dc3545' }}>
                        {formatCurrency(viewOrder.total - viewOrder.totalCost)}
                      </strong></p>
                    </div>
                  </div>
                </div>
              </div>
            </div>
            <div style={modalStyles.footer}>
              <button className="btn btn-secondary" onClick={closeModal}>
                Close
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

const modalStyles = {
  overlay: {
    position: 'fixed',
    top: 0,
    left: 0,
    right: 0,
    bottom: 0,
    backgroundColor: 'rgba(0, 0, 0, 0.5)',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    zIndex: 1000,
  },
  modal: {
    backgroundColor: 'white',
    borderRadius: '8px',
    width: '95%',
    maxWidth: '1000px',
    maxHeight: '90vh',
    overflow: 'auto',
  },
  header: {
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
    padding: '20px',
    borderBottom: '1px solid #e9ecef',
  },
  closeButton: {
    background: 'none',
    border: 'none',
    fontSize: '24px',
    cursor: 'pointer',
    padding: '0',
    width: '30px',
    height: '30px',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
  },
  footer: {
    padding: '20px',
    borderTop: '1px solid #e9ecef',
    display: 'flex',
    justifyContent: 'flex-end',
  },
};

export default Orders;
