import React, { useState, useEffect } from 'react';
import { productsAPI, uploadAPI } from '../services/api';
import { toast } from 'react-toastify';
import { FiPlus, FiEdit, FiTrash2, FiPackage, FiAlertTriangle, FiUpload, FiImage, FiX } from 'react-icons/fi';
import { productImages, getAllProductImages } from '../data/productImages';

const Products = () => {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showModal, setShowModal] = useState(false);
  const [editingProduct, setEditingProduct] = useState(null);
  const [formData, setFormData] = useState({
    name: '',
    imageUrl: '',
    costPrice: '',
  });
  const [showImageSelector, setShowImageSelector] = useState(false);
  const [availableImages] = useState(getAllProductImages());
  const [imageFilter, setImageFilter] = useState('');
  const [categories] = useState([
    'cleanser', 'moisturizer', 'serum', 'sunscreen', 'toner', 'mask', 'treatment', 'other'
  ]);

  useEffect(() => {
    fetchProducts();
  }, []);

  const fetchProducts = async () => {
    try {
      setLoading(true);
      const response = await productsAPI.getAll();
      setProducts(response.data);
    } catch (error) {
      toast.error('Failed to fetch products');
      console.error(error);
    } finally {
      setLoading(false);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      // Validate form data
      if (!formData.name.trim()) {
        toast.error('Product name is required');
        return;
      }
      
      if (!formData.costPrice || parseFloat(formData.costPrice) <= 0) {
        toast.error('Valid product cost is required');
        return;
      }

      const submitData = {
        name: formData.name.trim(),
        imageUrl: formData.imageUrl.trim(),
        costPrice: parseFloat(formData.costPrice)
      };

      console.log('Submitting product data:', {
        ...submitData,
        imageUrl: submitData.imageUrl.length > 50 ? `${submitData.imageUrl.substring(0, 50)}... (${submitData.imageUrl.length} chars)` : submitData.imageUrl
      });

      const loadingToast = toast.loading('Saving product...');

      if (editingProduct) {
        await productsAPI.update(editingProduct._id, submitData);
        toast.dismiss(loadingToast);
        toast.success('Product updated successfully');
      } else {
        const response = await productsAPI.create(submitData);
        console.log('Product created successfully:', response.data);
        toast.dismiss(loadingToast);
        toast.success('Product created successfully');
      }
      fetchProducts();
      closeModal();
    } catch (error) {
      console.error('Submit error details:', {
        message: error.message,
        response: error.response?.data,
        status: error.response?.status,
        statusText: error.response?.statusText
      });
      
      // Try to save with a placeholder image if the original fails due to size
      if (error.response?.status === 413 || error.response?.status === 500) {
        try {
          const fallbackData = {
            name: formData.name.trim(),
            imageUrl: 'https://images.unsplash.com/photo-1556909114-f6e7ad7d3136?w=400&h=400&fit=crop&crop=center', // Default image
            costPrice: parseFloat(formData.costPrice)
          };
          
          if (editingProduct) {
            await productsAPI.update(editingProduct._id, fallbackData);
            toast.success('Product updated successfully (with default image due to size limit)');
          } else {
            await productsAPI.create(fallbackData);
            toast.success('Product created successfully (with default image due to size limit)');
          }
          
          fetchProducts();
          closeModal();
          return;
        } catch (fallbackError) {
          toast.error('Failed to save product even with default image');
          return;
        }
      }
      
      toast.error(error.response?.data?.message || 'Failed to save product');
    }
  };

  const handleDelete = async (id) => {
    if (window.confirm('Are you sure you want to delete this product?')) {
      try {
        await productsAPI.delete(id);
        toast.success('Product deleted successfully');
        fetchProducts();
      } catch (error) {
        toast.error('Failed to delete product');
      }
    }
  };

  const openModal = (product = null) => {
    if (product) {
      setEditingProduct(product);
      setFormData({
        name: product.name,
        imageUrl: product.imageUrl || '',
        costPrice: product.costPrice,
      });
    } else {
      setEditingProduct(null);
      setFormData({
        name: '',
        imageUrl: '',
        costPrice: '',
      });
    }
    setShowModal(true);
  };

  const closeModal = () => {
    setShowModal(false);
    setEditingProduct(null);
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleFileUpload = async (e) => {
    const file = e.target.files[0];
    if (file) {
      if (file.type.startsWith('image/')) {
        const uploadToast = toast.loading('Uploading image...');
        
        try {
          const reader = new FileReader();
          reader.onload = async (event) => {
            try {
              const base64String = event.target.result;
              const fileName = file.name.replace(/\.[^/.]+$/, ""); // Remove extension
              
              // Upload to server
              const response = await uploadAPI.uploadImage(base64String, fileName);
              
              setFormData(prev => ({
                ...prev,
                imageUrl: `http://localhost:5000${response.data.imageUrl}`
              }));
              
              toast.dismiss(uploadToast);
              toast.success(`Image uploaded successfully (${Math.round(file.size / 1024)}KB)`);
            } catch (uploadError) {
              console.error('Upload error:', uploadError);
              toast.dismiss(uploadToast);
              
              // Fallback to base64 if upload fails
              const base64String = event.target.result;
              setFormData(prev => ({
                ...prev,
                imageUrl: base64String
              }));
              toast.warning('Using direct image data (upload service unavailable)');
            }
          };
          reader.readAsDataURL(file);
        } catch (error) {
          toast.dismiss(uploadToast);
          toast.error('Failed to process image');
        }
      } else {
        toast.error('Please select a valid image file');
      }
    }
  };

  const selectImageFromGallery = (imageUrl) => {
    setFormData(prev => ({
      ...prev,
      imageUrl: imageUrl
    }));
    setShowImageSelector(false);
  };

  const removeImage = () => {
    setFormData(prev => ({
      ...prev,
      imageUrl: ''
    }));
  };

  const filteredImages = availableImages.filter(image =>
    image.name.toLowerCase().includes(imageFilter.toLowerCase()) ||
    image.description.toLowerCase().includes(imageFilter.toLowerCase())
  );

  const formatCurrency = (amount) => {
    return new Intl.NumberFormat('en-BD', {
      style: 'currency',
      currency: 'BDT',
      minimumFractionDigits: 0,
      maximumFractionDigits: 0,
    }).format(amount || 0).replace('৳', '৳ ');
  };

  if (loading) {
    return <div className="container"><div className="loading">Loading products...</div></div>;
  }

  return (
    <div className="container" style={{ padding: '20px' }}>
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '30px' }}>
        <h1>Products</h1>
        <button className="btn btn-primary" onClick={() => openModal()}>
          <FiPlus style={{ marginRight: '8px' }} />
          Add Product
        </button>
      </div>

      {products.length === 0 ? (
        <div className="card text-center" style={{ padding: '40px' }}>
          <FiPackage size={60} color="#6c757d" style={{ marginBottom: '20px' }} />
          <h3>No products found</h3>
          <p>Start by adding your first product to the inventory.</p>
          <button className="btn btn-primary" onClick={() => openModal()}>
            Add Your First Product
          </button>
        </div>
      ) : (
        <div className="card">
          <div className="table-responsive">
            <table className="table">
              <thead>
                <tr>
                  <th>Image</th>
                  <th>Product Name</th>
                  <th>Product Cost</th>
                  <th>Actions</th>
                </tr>
              </thead>
              <tbody>
                {products.map((product) => (
                  <tr key={product._id}>
                    <td>
                      {product.imageUrl ? (
                        <img 
                          src={product.imageUrl} 
                          alt={product.name}
                          style={{ 
                            width: '60px', 
                            height: '60px', 
                            objectFit: 'cover', 
                            borderRadius: '8px',
                            border: '1px solid #e9ecef'
                          }}
                          onError={(e) => {
                            e.target.style.display = 'none';
                          }}
                        />
                      ) : (
                        <div style={{ 
                          width: '60px', 
                          height: '60px', 
                          backgroundColor: '#e9ecef', 
                          borderRadius: '8px',
                          display: 'flex',
                          alignItems: 'center',
                          justifyContent: 'center',
                          fontSize: '12px',
                          color: '#6c757d',
                          border: '1px solid #e9ecef'
                        }}>
                          No Image
                        </div>
                      )}
                    </td>
                    <td>
                      <div>
                        <div style={{ fontWeight: '500', fontSize: '16px' }}>{product.name}</div>
                        <div style={{ fontSize: '12px', color: '#6c757d' }}>
                          SKU: {product.sku}
                        </div>
                      </div>
                    </td>
                    <td>
                      <span style={{ fontWeight: '600', fontSize: '16px', color: '#333' }}>
                        ৳ {product.costPrice}
                      </span>
                    </td>
                    <td>
                      <button
                        className="btn btn-sm btn-primary mr-2"
                        onClick={() => openModal(product)}
                        title="Edit Product"
                      >
                        <FiEdit size={14} />
                      </button>
                      <button
                        className="btn btn-sm btn-danger"
                        onClick={() => handleDelete(product._id)}
                        title="Delete Product"
                      >
                        <FiTrash2 size={14} />
                      </button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      )}

      {/* Modal */}
      {showModal && (
        <div style={modalStyles.overlay}>
          <div style={modalStyles.modal}>
            <div style={modalStyles.header}>
              <h3>{editingProduct ? 'Edit Product' : 'Add New Product'}</h3>
              <button onClick={closeModal} style={modalStyles.closeButton}>×</button>
            </div>
            <form onSubmit={handleSubmit}>
              <div className="form-group">
                <label className="form-label">Product Name</label>
                <input
                  type="text"
                  name="name"
                  value={formData.name}
                  onChange={handleInputChange}
                  className="form-control"
                  required
                  placeholder="Enter product name"
                />
              </div>

              <div className="form-group">
                <label className="form-label">Product Image</label>
                
                {/* Image Preview */}
                {formData.imageUrl && (
                  <div style={{ marginBottom: '15px', position: 'relative', display: 'inline-block' }}>
                    <img 
                      src={formData.imageUrl} 
                      alt="Product preview" 
                      style={{ 
                        width: '150px', 
                        height: '150px', 
                        objectFit: 'cover', 
                        borderRadius: '8px',
                        border: '2px solid #e9ecef'
                      }}
                      onError={(e) => {
                        e.target.style.display = 'none';
                      }}
                    />
                    <button
                      type="button"
                      onClick={removeImage}
                      style={{
                        position: 'absolute',
                        top: '-8px',
                        right: '-8px',
                        background: '#dc3545',
                        color: 'white',
                        border: 'none',
                        borderRadius: '50%',
                        width: '24px',
                        height: '24px',
                        display: 'flex',
                        alignItems: 'center',
                        justifyContent: 'center',
                        cursor: 'pointer'
                      }}
                    >
                      <FiX size={12} />
                    </button>
                  </div>
                )}

                {/* Upload Options */}
                <div style={{ display: 'flex', gap: '10px', marginBottom: '15px' }}>
                  <label className="btn btn-outline-primary" style={{ cursor: 'pointer', display: 'flex', alignItems: 'center', gap: '8px' }}>
                    <FiUpload size={16} />
                    Upload from PC
                    <input
                      type="file"
                      accept="image/*"
                      onChange={handleFileUpload}
                      style={{ display: 'none' }}
                    />
                  </label>
                  
                  <button
                    type="button"
                    className="btn btn-outline-secondary"
                    onClick={() => setShowImageSelector(true)}
                    style={{ display: 'flex', alignItems: 'center', gap: '8px' }}
                  >
                    <FiImage size={16} />
                    Choose from Gallery
                  </button>
                </div>

                <div style={{ 
                  fontSize: '12px', 
                  color: '#6c757d', 
                  marginBottom: '15px',
                  padding: '8px',
                  backgroundColor: '#e8f5e8',
                  borderRadius: '4px',
                  border: '1px solid #28a745'
                }}>
                  ✅ <strong>Upload any size image!</strong> Large images will automatically use a fallback if needed. 
                  Gallery images are optimized for best performance.
                </div>

                {/* Manual URL Input */}
                <input
                  type="url"
                  name="imageUrl"
                  value={formData.imageUrl.includes('localhost:5000') ? '[Uploaded Image]' : formData.imageUrl}
                  onChange={handleInputChange}
                  className="form-control"
                  placeholder="Or enter image URL directly"
                  disabled={formData.imageUrl.includes('localhost:5000') || formData.imageUrl.startsWith('data:image/')}
                />
                
                {(formData.imageUrl.includes('localhost:5000') || formData.imageUrl.startsWith('data:image/')) && (
                  <small className="text-muted" style={{ marginTop: '5px', display: 'block' }}>
                    📷 Image uploaded from your computer
                  </small>
                )}
              </div>

              <div className="form-group">
                <label className="form-label">Product Cost (৳)</label>
                <input
                  type="number"
                  name="costPrice"
                  value={formData.costPrice}
                  onChange={handleInputChange}
                  className="form-control"
                  step="1"
                  min="0"
                  required
                  placeholder="0"
                />
              </div>



              <div style={modalStyles.footer}>
                <button type="button" className="btn btn-secondary mr-2" onClick={closeModal}>
                  Cancel
                </button>
                <button type="submit" className="btn btn-primary">
                  {editingProduct ? 'Update Product' : 'Create Product'}
                </button>
              </div>
            </form>
          </div>
        </div>
      )}

      {/* Image Selector Modal */}
      {showImageSelector && (
        <div style={modalStyles.overlay}>
          <div style={{...modalStyles.modal, maxWidth: '900px'}}>
            <div style={modalStyles.header}>
              <h3>Choose Product Image</h3>
              <button onClick={() => setShowImageSelector(false)} style={modalStyles.closeButton}>×</button>
            </div>
            <div style={{ padding: '20px' }}>
              {/* Search Filter */}
              <div className="form-group" style={{ marginBottom: '20px' }}>
                <input
                  type="text"
                  className="form-control"
                  placeholder="Search images..."
                  value={imageFilter}
                  onChange={(e) => setImageFilter(e.target.value)}
                />
              </div>

              {/* Image Grid */}
              <div style={{ 
                display: 'grid', 
                gridTemplateColumns: 'repeat(auto-fill, minmax(120px, 1fr))', 
                gap: '15px',
                maxHeight: '400px',
                overflowY: 'auto'
              }}>
                {filteredImages.map((image, index) => (
                  <div 
                    key={index}
                    onClick={() => selectImageFromGallery(image.url)}
                    style={{ 
                      cursor: 'pointer',
                      border: '2px solid transparent',
                      borderRadius: '8px',
                      overflow: 'hidden',
                      transition: 'all 0.2s',
                      ':hover': {
                        border: '2px solid #007bff'
                      }
                    }}
                    onMouseEnter={(e) => e.target.style.borderColor = '#007bff'}
                    onMouseLeave={(e) => e.target.style.borderColor = 'transparent'}
                  >
                    <img 
                      src={image.url} 
                      alt={image.name}
                      style={{ 
                        width: '100%', 
                        height: '120px', 
                        objectFit: 'cover'
                      }}
                    />
                    <div style={{ 
                      padding: '8px', 
                      backgroundColor: '#f8f9fa',
                      fontSize: '12px',
                      textAlign: 'center'
                    }}>
                      {image.name}
                    </div>
                  </div>
                ))}
              </div>

              {filteredImages.length === 0 && (
                <div style={{ textAlign: 'center', padding: '40px', color: '#6c757d' }}>
                  <FiImage size={48} />
                  <p style={{ marginTop: '10px' }}>No images found matching your search.</p>
                </div>
              )}
            </div>
            <div style={modalStyles.footer}>
              <button 
                className="btn btn-secondary" 
                onClick={() => setShowImageSelector(false)}
              >
                Cancel
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

const modalStyles = {
  overlay: {
    position: 'fixed',
    top: 0,
    left: 0,
    right: 0,
    bottom: 0,
    backgroundColor: 'rgba(0, 0, 0, 0.5)',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    zIndex: 1000,
  },
  modal: {
    backgroundColor: 'white',
    borderRadius: '8px',
    width: '90%',
    maxWidth: '800px',
    maxHeight: '90vh',
    overflow: 'auto',
  },
  header: {
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
    padding: '20px',
    borderBottom: '1px solid #e9ecef',
  },
  closeButton: {
    background: 'none',
    border: 'none',
    fontSize: '24px',
    cursor: 'pointer',
    padding: '0',
    width: '30px',
    height: '30px',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
  },
  footer: {
    padding: '20px',
    borderTop: '1px solid #e9ecef',
    display: 'flex',
    justifyContent: 'flex-end',
  },
};

export default Products;
