import React, { useState, useEffect } from 'react';
import { ordersAPI, productsAPI, employeesAPI } from '../services/api';
import { useAuth } from '../context/AuthContext';
import { toast } from 'react-toastify';
import { FiPlus, FiEye, FiX, FiShoppingCart, FiTrash2, FiEdit } from 'react-icons/fi';

const Orders = () => {
  const { user, isAdmin, isEmployee } = useAuth();
  const [orders, setOrders] = useState([]);
  const [products, setProducts] = useState([]);
  const [employees, setEmployees] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showModal, setShowModal] = useState(false);
  const [viewOrder, setViewOrder] = useState(null);
  const [editingOrder, setEditingOrder] = useState(null);
  const [formData, setFormData] = useState({
    customerName: '',
    customerPhone: '',
    employeeId: '',
    items: [{ productId: '', quantity: 1, customPrice: 0 }],
  });

  useEffect(() => {
    fetchOrders();
    fetchProducts();
    fetchEmployees();
  }, []);

  const fetchOrders = async () => {
    try {
      setLoading(true);
      const response = await ordersAPI.getAll();
      setOrders(response.data);
    } catch (error) {
      toast.error('Failed to fetch orders');
      console.error(error);
    } finally {
      setLoading(false);
    }
  };

  const fetchProducts = async () => {
    try {
      const response = await productsAPI.getAll({ isActive: true });
      setProducts(response.data);
    } catch (error) {
      console.error('Failed to fetch products:', error);
    }
  };

  const fetchEmployees = async () => {
    try {
      if (isAdmin) {
        const response = await employeesAPI.getAll({ isActive: true });
        setEmployees(response.data);
      }
    } catch (error) {
      console.error('Failed to fetch employees:', error);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (editingOrder) {
        await ordersAPI.update(editingOrder._id, formData);
        toast.success('Order updated successfully');
      } else {
        await ordersAPI.create(formData);
        toast.success('Order created successfully');
      }
      fetchOrders();
      closeModal();
    } catch (error) {
      toast.error(error.response?.data?.message || 'Failed to save order');
    }
  };



  const handleDeleteOrder = async (orderId, orderNumber) => {
    if (window.confirm(`Are you sure you want to delete order ${orderNumber}? This action cannot be undone.`)) {
      try {
        await ordersAPI.delete(orderId);
        toast.success('Order deleted successfully');
        fetchOrders();
      } catch (error) {
        toast.error('Failed to delete order');
        console.error('Delete error:', error);
      }
    }
  };

  const openEditModal = (order) => {
    setEditingOrder(order);
    setFormData({
      customerName: order.customerName,
      customerPhone: order.customerPhone,
      employeeId: order.employee?._id || '',
      items: order.items.map(item => ({
        productId: item.product._id,
        quantity: item.quantity,
        customPrice: item.priceAtTime
      }))
    });
    setShowModal(true);
  };



  const openModal = () => {
    setFormData({
      customerName: '',
      customerPhone: '',
      employeeId: '',
      items: [{ productId: '', quantity: 1, customPrice: 0 }],
    });
    setShowModal(true);
  };

  const closeModal = () => {
    setShowModal(false);
    setViewOrder(null);
    setEditingOrder(null);
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleItemChange = (index, field, value) => {
    const newItems = [...formData.items];
    newItems[index][field] = value;
    
    // When product is selected, auto-populate custom price with product's selling price
    if (field === 'productId' && value) {
      const product = products.find(p => p._id === value);
      if (product) {
        newItems[index]['customPrice'] = product.sellingPrice;
      }
    }
    
    setFormData(prev => ({
      ...prev,
      items: newItems
    }));
  };

  const addItem = () => {
    setFormData(prev => ({
      ...prev,
      items: [...prev.items, { productId: '', quantity: 1, customPrice: 0 }]
    }));
  };

  const removeItem = (index) => {
    if (formData.items.length > 1) {
      const newItems = formData.items.filter((_, i) => i !== index);
      setFormData(prev => ({
        ...prev,
        items: newItems
      }));
    }
  };

  const calculateTotal = () => {
    return formData.items.reduce((sum, item) => {
      const customPrice = parseFloat(item.customPrice) || 0;
      return sum + (customPrice * item.quantity);
    }, 0);
  };

  const formatCurrency = (amount) => {
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: 'BDT',
    }).format(amount || 0);
  };



  if (loading) {
    return <div className="container"><div className="loading">Loading orders...</div></div>;
  }

  return (
    <div className="container" style={{ padding: '20px' }}>
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '30px' }}>
        <h1>Orders</h1>
        <button className="btn btn-primary" onClick={openModal}>
          <FiPlus style={{ marginRight: '8px' }} />
          Create Order
        </button>
      </div>

      {orders.length === 0 ? (
        <div className="card text-center" style={{ padding: '40px' }}>
          <FiShoppingCart size={60} color="#6c757d" style={{ marginBottom: '20px' }} />
          <h3>No orders found</h3>
          <p>Start by creating your first order.</p>
          <button className="btn btn-primary" onClick={openModal}>
            Create Your First Order
          </button>
        </div>
      ) : (
        <div className="card">
          <div className="table-responsive">
            <table className="table">
              <thead>
                <tr>
                  <th>Order #</th>
                  <th>Customer</th>
                  <th>Items</th>
                  <th>Total</th>
                  <th>Profit</th>
                  <th>Employee</th>
                  <th>Date</th>
                  <th>Actions</th>
                </tr>
              </thead>
              <tbody>
                {orders.map((order) => (
                  <tr key={order._id}>
                    <td style={{ fontWeight: '600' }}>{order.orderNumber}</td>
                    <td>
                      <div>
                        <div style={{ fontWeight: '500' }}>{order.customerName}</div>
                        <div style={{ fontSize: '12px', color: '#6c757d' }}>
                          {order.customerEmail}
                        </div>
                      </div>
                    </td>
                    <td>{order.items.length} items</td>
                    <td>{formatCurrency(order.total)}</td>
                    <td>
                      <span style={{ color: order.total - order.totalCost > 0 ? '#28a745' : '#dc3545' }}>
                        {formatCurrency(order.total - order.totalCost)}
                      </span>
                    </td>
                    <td>
                      {order.employee ? (
                        <div>
                          <div style={{ fontWeight: '500', color: '#28a745' }}>
                            {order.employee.firstName} {order.employee.lastName}
                          </div>
                          <div style={{ fontSize: '12px', color: '#6c757d' }}>
                            ৳{order.employee.commissionPerOrder || 0} commission
                          </div>
                        </div>
                      ) : (
                        <span style={{ color: '#6c757d', fontStyle: 'italic' }}>No employee</span>
                      )}
                    </td>
                    <td>{new Date(order.createdAt).toLocaleDateString()}</td>
                    <td>
                      <button
                        className="btn btn-sm btn-primary mr-2"
                        onClick={() => setViewOrder(order)}
                        title="View Order"
                      >
                        <FiEye size={14} />
                      </button>
                      <button
                        className="btn btn-sm btn-secondary mr-2"
                        onClick={() => openEditModal(order)}
                        title="Edit Order"
                      >
                        <FiEdit size={14} />
                      </button>
                      <button
                        className="btn btn-sm btn-danger"
                        onClick={() => handleDeleteOrder(order._id, order.orderNumber)}
                        title="Delete Order"
                      >
                        <FiTrash2 size={14} />
                      </button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      )}

      {/* Create Order Modal */}
      {showModal && (
        <div style={modalStyles.overlay}>
          <div style={modalStyles.modal}>
            <div style={modalStyles.header}>
              <h3>{editingOrder ? 'Edit Order' : 'Create New Order'}</h3>
              <button onClick={closeModal} style={modalStyles.closeButton}>×</button>
            </div>
            <form onSubmit={handleSubmit}>
              <div style={{ padding: '20px' }}>
                <div className="row">
                  <div className="col-md-4">
                    <div className="form-group">
                      <label className="form-label">Customer Name</label>
                      <input
                        type="text"
                        name="customerName"
                        value={formData.customerName}
                        onChange={handleInputChange}
                        className="form-control"
                        required
                      />
                    </div>
                  </div>
                  <div className="col-md-4">
                    <div className="form-group">
                      <label className="form-label">Phone</label>
                      <input
                        type="tel"
                        name="customerPhone"
                        value={formData.customerPhone}
                        onChange={handleInputChange}
                        className="form-control"
                        required
                      />
                    </div>
                  </div>
                  <div className="col-md-4">
                    <div className="form-group">
                      <label className="form-label">Employee (Optional)</label>
                      <select
                        name="employeeId"
                        value={formData.employeeId}
                        onChange={handleInputChange}
                        className="form-control"
                      >
                        <option value="">No Employee</option>
                        {employees.map(employee => (
                          <option key={employee._id} value={employee._id}>
                            {employee.firstName} {employee.lastName} - {employee.commissionPerOrder || 0}৳/order
                          </option>
                        ))}
                      </select>
                    </div>
                  </div>
                </div>

                <h4>Order Items</h4>
                {formData.items.map((item, index) => (
                  <div key={index} className="card" style={{ marginBottom: '15px', padding: '15px' }}>
                    <div className="row">
                      <div className="col-md-4">
                        <label className="form-label">Product</label>
                        <select
                          value={item.productId}
                          onChange={(e) => handleItemChange(index, 'productId', e.target.value)}
                          className="form-control"
                          required
                        >
                          <option value="">Select Product</option>
                          {products.map(product => (
                            <option key={product._id} value={product._id}>
                              {product.name}
                            </option>
                          ))}
                        </select>
                      </div>
                      <div className="col-md-3">
                        <label className="form-label">Custom Price (৳)</label>
                        <input
                          type="number"
                          value={item.customPrice}
                          onChange={(e) => handleItemChange(index, 'customPrice', parseFloat(e.target.value) || 0)}
                          className="form-control"
                          min="0"
                          step="0.01"
                          required
                          placeholder="Enter price"
                        />
                      </div>
                      <div className="col-md-3">
                        <label className="form-label">Quantity</label>
                        <input
                          type="number"
                          value={item.quantity}
                          onChange={(e) => handleItemChange(index, 'quantity', parseInt(e.target.value) || 1)}
                          className="form-control"
                          min="1"
                          required
                        />
                      </div>
                      <div className="col-md-2">
                        <label className="form-label">&nbsp;</label>
                        <button
                          type="button"
                          className="btn btn-danger btn-sm"
                          onClick={() => removeItem(index)}
                          disabled={formData.items.length === 1}
                          style={{ width: '100%' }}
                        >
                          <FiX />
                        </button>
                      </div>
                    </div>
                    {item.productId && item.customPrice > 0 && (
                      <div className="row mt-2">
                        <div className="col-12">
                          <small className="text-muted">
                            Item Total: <strong>{formatCurrency(item.customPrice * item.quantity)}</strong>
                          </small>
                        </div>
                      </div>
                    )}
                  </div>
                ))}

                <button type="button" className="btn btn-secondary mb-3" onClick={addItem}>
                  Add Item
                </button>

                <div className="card" style={{ backgroundColor: '#f8f9fa', marginTop: '20px' }}>
                  <div style={{ padding: '15px' }}>
                    <h4>Total Bill</h4>
                    <p style={{ fontSize: '24px', fontWeight: 'bold', color: '#007bff', margin: '10px 0' }}>
                      {formatCurrency(calculateTotal())}
                    </p>
                  </div>
                </div>
              </div>

              <div style={modalStyles.footer}>
                <button type="button" className="btn btn-secondary mr-2" onClick={closeModal}>
                  Cancel
                </button>
                <button type="submit" className="btn btn-primary">
                  {editingOrder ? 'Update Order' : 'Create Order'}
                </button>
              </div>
            </form>
          </div>
        </div>
      )}

      {/* View Order Modal */}
      {viewOrder && (
        <div style={modalStyles.overlay}>
          <div style={modalStyles.modal}>
            <div style={modalStyles.header}>
              <h3>Order Details - {viewOrder.orderNumber}</h3>
              <button onClick={closeModal} style={modalStyles.closeButton}>×</button>
            </div>
            <div style={{ padding: '20px' }}>
              <div className="row">
                <div className="col-md-6">
                  <h4>Customer Information</h4>
                  <p><strong>Name:</strong> {viewOrder.customerName}</p>
                  <p><strong>Email:</strong> {viewOrder.customerEmail}</p>
                  <p><strong>Phone:</strong> {viewOrder.customerPhone}</p>
                </div>
                <div className="col-md-6">
                  <h4>Order Information</h4>
                  <p><strong>Employee:</strong> {viewOrder.employee ? `${viewOrder.employee.firstName} ${viewOrder.employee.lastName}` : 'No employee assigned'}</p>
                  {viewOrder.employee && (
                    <p><strong>Commission:</strong> ৳{viewOrder.employee.commissionPerOrder || 0}</p>
                  )}
                  <p><strong>Date:</strong> {new Date(viewOrder.createdAt).toLocaleDateString()}</p>
                </div>
              </div>
              
              <h4>Order Items</h4>
              <table className="table">
                <thead>
                  <tr>
                    <th>Product</th>
                    <th>Quantity</th>
                    <th>Price</th>
                    <th>Total</th>
                  </tr>
                </thead>
                <tbody>
                  {viewOrder.items.map((item, index) => (
                    <tr key={index}>
                      <td>{item.product?.name || 'Unknown Product'}</td>
                      <td>{item.quantity}</td>
                      <td>{formatCurrency(item.priceAtTime)}</td>
                      <td>{formatCurrency(item.priceAtTime * item.quantity)}</td>
                    </tr>
                  ))}
                </tbody>
              </table>

              <div className="row">
                <div className="col-md-6">
                  {viewOrder.notes && (
                    <>
                      <h4>Notes</h4>
                      <p>{viewOrder.notes}</p>
                    </>
                  )}
                </div>
                <div className="col-md-6">
                  <div className="card">
                    <div style={{ padding: '15px' }}>
                      <h4>Order Summary</h4>
                      <p>Subtotal: <strong>{formatCurrency(viewOrder.subtotal)}</strong></p>
                      <p>Tax: <strong>{formatCurrency(viewOrder.tax)}</strong></p>
                      <p>Shipping: <strong>{formatCurrency(viewOrder.shipping)}</strong></p>
                      <p>Discount: <strong>-{formatCurrency(viewOrder.discount)}</strong></p>
                      <hr />
                      <p>Total: <strong>{formatCurrency(viewOrder.total)}</strong></p>
                      <p>Profit: <strong style={{ color: viewOrder.total - viewOrder.totalCost > 0 ? '#28a745' : '#dc3545' }}>
                        {formatCurrency(viewOrder.total - viewOrder.totalCost)}
                      </strong></p>
                    </div>
                  </div>
                </div>
              </div>
            </div>
            <div style={modalStyles.footer}>
              <button className="btn btn-secondary" onClick={closeModal}>
                Close
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

const modalStyles = {
  overlay: {
    position: 'fixed',
    top: 0,
    left: 0,
    right: 0,
    bottom: 0,
    backgroundColor: 'rgba(0, 0, 0, 0.5)',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    zIndex: 1000,
  },
  modal: {
    backgroundColor: 'white',
    borderRadius: '8px',
    width: '95%',
    maxWidth: '1000px',
    maxHeight: '90vh',
    overflow: 'auto',
  },
  header: {
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
    padding: '20px',
    borderBottom: '1px solid #e9ecef',
  },
  closeButton: {
    background: 'none',
    border: 'none',
    fontSize: '24px',
    cursor: 'pointer',
    padding: '0',
    width: '30px',
    height: '30px',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
  },
  footer: {
    padding: '20px',
    borderTop: '1px solid #e9ecef',
    display: 'flex',
    justifyContent: 'flex-end',
  },
};

export default Orders;
