import React, { useState, useEffect } from 'react';
import { employeesAPI } from '../services/api';
import { toast } from 'react-toastify';
import { FiPlus, FiEdit, FiUserX, FiUsers, FiDollarSign } from 'react-icons/fi';

const Employees = () => {
  const [employees, setEmployees] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showModal, setShowModal] = useState(false);
  const [editingEmployee, setEditingEmployee] = useState(null);
  const [showSalaryModal, setShowSalaryModal] = useState(false);
  const [salaryData, setSalaryData] = useState(null);
  const [selectedEmployee, setSelectedEmployee] = useState(null);
  const [salaryPeriod, setSalaryPeriod] = useState({
    year: new Date().getFullYear(),
    month: new Date().getMonth() + 1,
  });
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    email: '',
    phone: '',
    position: '',
    department: 'sales', // Default to sales since it's commission-based
    baseSalary: '0', // Set to 0 since salary is commission-based
    commissionPerOrder: '0',
    hireDate: '',
    address: {
      street: '',
      city: '',
      state: '',
      zipCode: '',
      country: '',
    },
    emergencyContact: {
      name: '',
      phone: '',
      relationship: '',
    },
  });

  const positions = ['manager', 'sales_associate', 'inventory_clerk', 'customer_service', 'admin', 'other'];
  const departments = ['sales', 'inventory', 'customer_service', 'administration', 'marketing', 'other'];

  useEffect(() => {
    fetchEmployees();
  }, []);

  const fetchEmployees = async () => {
    try {
      setLoading(true);
      const response = await employeesAPI.getAll({ isActive: true });
      setEmployees(response.data);
    } catch (error) {
      toast.error('Failed to fetch employees');
      console.error(error);
    } finally {
      setLoading(false);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (editingEmployee) {
        await employeesAPI.update(editingEmployee._id, formData);
        toast.success('Employee updated successfully');
      } else {
        await employeesAPI.create(formData);
        toast.success('Employee added successfully');
      }
      fetchEmployees();
      closeModal();
    } catch (error) {
      toast.error(error.response?.data?.message || 'Failed to save employee');
    }
  };

  const handleTerminate = async (id) => {
    if (window.confirm('Are you sure you want to terminate this employee?')) {
      try {
        await employeesAPI.terminate(id, { terminationDate: new Date() });
        toast.success('Employee terminated successfully');
        fetchEmployees();
      } catch (error) {
        toast.error('Failed to terminate employee');
      }
    }
  };

  const viewEmployeeSalary = async (employee) => {
    try {
      setSelectedEmployee(employee);
      // Get employee orders to calculate total salary
      const response = await employeesAPI.getEmployeeOrders(employee._id);
      setSalaryData(response.data);
      setShowSalaryModal(true);
    } catch (error) {
      toast.error('Failed to load salary data');
      console.error('Salary error:', error);
    }
  };

  const openModal = (employee = null) => {
    if (employee) {
      setEditingEmployee(employee);
      setFormData({
        firstName: employee.firstName,
        lastName: employee.lastName,
        email: employee.email,
        phone: employee.phone,
        position: employee.position,
        department: employee.department,
        baseSalary: employee.baseSalary,
        commissionPerOrder: employee.commissionPerOrder || 0,
        hireDate: new Date(employee.hireDate).toISOString().split('T')[0],
        address: employee.address || {
          street: '',
          city: '',
          state: '',
          zipCode: '',
          country: '',
        },
        emergencyContact: employee.emergencyContact || {
          name: '',
          phone: '',
          relationship: '',
        },
      });
    } else {
      setEditingEmployee(null);
      setFormData({
        firstName: '',
        lastName: '',
        email: '',
        phone: '',
        position: '',
        department: 'sales', // Default to sales
        baseSalary: '0', // Set to 0 for commission-based salary
        commissionPerOrder: '0',
        hireDate: '',
        address: {
          street: '',
          city: '',
          state: '',
          zipCode: '',
          country: '',
        },
        emergencyContact: {
          name: '',
          phone: '',
          relationship: '',
        },
      });
    }
    setShowModal(true);
  };

  const closeModal = () => {
    setShowModal(false);
    setEditingEmployee(null);
  };

  const openSalaryModal = async () => {
    try {
      const response = await employeesAPI.getSalarySummary(salaryPeriod.year, salaryPeriod.month);
      setSalaryData(response.data);
      setShowSalaryModal(true);
    } catch (error) {
      toast.error('Failed to fetch salary data');
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    if (name.includes('.')) {
      const [parent, child] = name.split('.');
      setFormData(prev => ({
        ...prev,
        [parent]: {
          ...prev[parent],
          [child]: value
        }
      }));
    } else {
      setFormData(prev => ({
        ...prev,
        [name]: value
      }));
    }
  };

  const formatCurrency = (amount) => {
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: 'BDT',
    }).format(amount || 0);
  };

  if (loading) {
    return <div className="container"><div className="loading">Loading employees...</div></div>;
  }

  return (
    <div className="container" style={{ padding: '20px' }}>
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '30px' }}>
        <h1>Employees</h1>
        <div>
          <button className="btn btn-secondary mr-2" onClick={openSalaryModal}>
            <FiDollarSign style={{ marginRight: '8px' }} />
            View Salaries
          </button>
          <button className="btn btn-primary" onClick={() => openModal()}>
            <FiPlus style={{ marginRight: '8px' }} />
            Add Employee
          </button>
        </div>
      </div>

      {employees.length === 0 ? (
        <div className="card text-center" style={{ padding: '40px' }}>
          <FiUsers size={60} color="#6c757d" style={{ marginBottom: '20px' }} />
          <h3>No employees found</h3>
          <p>Start by adding your first team member.</p>
          <button className="btn btn-primary" onClick={() => openModal()}>
            Add Your First Employee
          </button>
        </div>
      ) : (
        <div className="card">
          <div className="table-responsive">
            <table className="table">
              <thead>
                <tr>
                  <th>Name</th>
                  <th>Position</th>
                  <th>Commission Per Order</th>
                  <th>Hire Date</th>
                  <th>Actions</th>
                </tr>
              </thead>
              <tbody>
                {employees.map((employee) => (
                  <tr key={employee._id}>
                    <td>
                      <div>
                        <div style={{ fontWeight: '500' }}>
                          {employee.firstName} {employee.lastName}
                        </div>
                        <div style={{ fontSize: '12px', color: '#6c757d' }}>
                          {employee.phone}
                        </div>
                      </div>
                    </td>
                    <td>
                      <span className="badge badge-primary">
                        {employee.position.replace('_', ' ')}
                      </span>
                    </td>
                    <td>
                      <div style={{ textAlign: 'center' }}>
                        <div style={{ fontWeight: '500', color: '#28a745' }}>
                          {employee.commissionPerOrder || 0} per order
                        </div>
                      </div>
                    </td>
                    <td>{new Date(employee.hireDate).toLocaleDateString()}</td>
                    <td>
                      <button
                        className="btn btn-sm btn-primary mr-2"
                        onClick={() => openModal(employee)}
                        title="Edit Employee"
                      >
                        <FiEdit size={14} />
                      </button>
                      <button
                        className="btn btn-sm btn-success mr-2"
                        onClick={() => viewEmployeeSalary(employee)}
                        title="View Salary"
                      >
                        <FiDollarSign size={14} />
                      </button>
                      <button
                        className="btn btn-sm btn-danger"
                        onClick={() => handleTerminate(employee._id)}
                        title="Terminate Employee"
                      >
                        <FiUserX size={14} />
                      </button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      )}

      {/* Employee Modal */}
      {showModal && (
        <div style={modalStyles.overlay}>
          <div style={modalStyles.modal}>
            <div style={modalStyles.header}>
              <h3>{editingEmployee ? 'Edit Employee' : 'Add New Employee'}</h3>
              <button onClick={closeModal} style={modalStyles.closeButton}></button>
            </div>
            <form onSubmit={handleSubmit}>
              <div style={{ padding: '20px' }}>
                <div className="row">
                  <div className="col-md-6">
                    <div className="form-group">
                      <label className="form-label">First Name</label>
                      <input
                        type="text"
                        name="firstName"
                        value={formData.firstName}
                        onChange={handleInputChange}
                        className="form-control"
                        required
                      />
                    </div>
                  </div>
                  <div className="col-md-6">
                    <div className="form-group">
                      <label className="form-label">Last Name</label>
                      <input
                        type="text"
                        name="lastName"
                        value={formData.lastName}
                        onChange={handleInputChange}
                        className="form-control"
                        required
                      />
                    </div>
                  </div>
                </div>

                <div className="row">
                  <div className="col-md-6">
                    <div className="form-group">
                      <label className="form-label">Email</label>
                      <input
                        type="email"
                        name="email"
                        value={formData.email}
                        onChange={handleInputChange}
                        className="form-control"
                        required
                      />
                    </div>
                  </div>
                  <div className="col-md-6">
                    <div className="form-group">
                      <label className="form-label">Phone</label>
                      <input
                        type="tel"
                        name="phone"
                        value={formData.phone}
                        onChange={handleInputChange}
                        className="form-control"
                        required
                      />
                    </div>
                  </div>
                </div>

                <div className="row">
                  <div className="col-md-6">
                    <div className="form-group">
                      <label className="form-label">Position</label>
                      <select
                        name="position"
                        value={formData.position}
                        onChange={handleInputChange}
                        className="form-control"
                        required
                      >
                        <option value="">Select Position</option>
                        {positions.map(pos => (
                          <option key={pos} value={pos}>
                            {pos.replace('_', ' ').toUpperCase()}
                          </option>
                        ))}
                      </select>
                    </div>
                  </div>
                  <div className="col-md-6">
                    <div className="form-group">
                      <label className="form-label">Department</label>
                      <select
                        name="department"
                        value={formData.department}
                        onChange={handleInputChange}
                        className="form-control"
                        required
                      >
                        <option value="">Select Department</option>
                        {departments.map(dept => (
                          <option key={dept} value={dept}>
                            {dept.replace('_', ' ').toUpperCase()}
                          </option>
                        ))}
                      </select>
                    </div>
                  </div>
                </div>

                <div className="row">
                  <div className="col-md-4">
                    <div className="form-group">
                      <label className="form-label">Hire Date</label>
                      <input
                        type="date"
                        name="hireDate"
                        value={formData.hireDate}
                        onChange={handleInputChange}
                        className="form-control"
                        required
                      />
                    </div>
                  </div>
                  <div className="col-md-4">
                    <div className="form-group">
                      <label className="form-label">Base Salary (Annual) - Taka</label>
                      <input
                        type="number"
                        name="baseSalary"
                        value={formData.baseSalary}
                        onChange={handleInputChange}
                        className="form-control"
                        min="0"
                        placeholder="0 (Commission based)"
                        required
                      />
                    </div>
                  </div>
                  <div className="col-md-4">
                    <div className="form-group">
                      <label className="form-label">Commission Per Order ()</label>
                      <input
                        type="number"
                        name="commissionPerOrder"
                        value={formData.commissionPerOrder}
                        onChange={handleInputChange}
                        className="form-control"
                        min="0"
                        step="1"
                        placeholder="e.g., 20, 30"
                      />
                    </div>
                  </div>
                </div>

                <div className="row">
                  <div className="col-md-12">
                    <div className="form-group">
                      <div style={{ 
                        padding: '15px', 
                        backgroundColor: '#e8f5e8', 
                        border: '1px solid #28a745', 
                        borderRadius: '5px',
                        textAlign: 'center'
                      }}>
                        <h5 style={{ color: '#28a745', margin: '0' }}>
                           Salary Structure: Custom Commission Per Order
                        </h5>
                        <p style={{ margin: '5px 0 0 0', fontSize: '14px', color: '#155724' }}>
                          Each employee earns a custom amount (e.g., 20-30 taka) for every order they process
                        </p>
                      </div>
                    </div>
                  </div>
                </div>
              </div>

              <div style={modalStyles.footer}>
                <button type="button" className="btn btn-secondary mr-2" onClick={closeModal}>
                  Cancel
                </button>
                <button type="submit" className="btn btn-primary">
                  {editingEmployee ? 'Update Employee' : 'Add Employee'}
                </button>
              </div>
            </form>
          </div>
        </div>
      )}

      {/* Salary Modal */}
      {showSalaryModal && salaryData && (
        <div style={modalStyles.overlay}>
          <div style={modalStyles.modal}>
            <div style={modalStyles.header}>
              <h3>Salary Summary - {salaryPeriod.month}/{salaryPeriod.year}</h3>
              <button onClick={() => setShowSalaryModal(false)} style={modalStyles.closeButton}></button>
            </div>
            <div style={{ padding: '20px' }}>
              <div className="row mb-3">
                <div className="col-md-6">
                  <label className="form-label">Year</label>
                  <input
                    type="number"
                    value={salaryPeriod.year}
                    onChange={(e) => setSalaryPeriod(prev => ({ ...prev, year: parseInt(e.target.value) }))}
                    className="form-control"
                    min="2020"
                    max="2030"
                  />
                </div>
                <div className="col-md-6">
                  <label className="form-label">Month</label>
                  <select
                    value={salaryPeriod.month}
                    onChange={(e) => setSalaryPeriod(prev => ({ ...prev, month: parseInt(e.target.value) }))}
                    className="form-control"
                  >
                    {Array.from({ length: 12 }, (_, i) => (
                      <option key={i + 1} value={i + 1}>
                        {new Date(2023, i, 1).toLocaleString('default', { month: 'long' })}
                      </option>
                    ))}
                  </select>
                </div>
              </div>
              <button className="btn btn-primary mb-3" onClick={openSalaryModal}>
                Update
              </button>

              <div className="card mb-3">
                <div style={{ padding: '15px' }}>
                  <h4>Summary</h4>
                  <p>Total Employees: <strong>{salaryData.totalEmployees}</strong></p>
                  <p>Monthly Sales: <strong>{formatCurrency(salaryData.monthlySales)}</strong></p>
                  <p>Total Salary Expense: <strong>{formatCurrency(salaryData.totalSalaryExpense)}</strong></p>
                </div>
              </div>

              <table className="table">
                <thead>
                  <tr>
                    <th>Employee</th>
                    <th>Position</th>
                    <th>Base Salary</th>
                    <th>Commission</th>
                    <th>Total Salary</th>
                  </tr>
                </thead>
                <tbody>
                  {salaryData.salaryDetails.map((detail, index) => (
                    <tr key={index}>
                      <td>{detail.employee.name}</td>
                      <td>
                        <span className="badge badge-secondary">
                          {detail.employee.position.replace('_', ' ')}
                        </span>
                      </td>
                      <td>{formatCurrency(detail.baseSalary)}</td>
                      <td>{formatCurrency(detail.commissionAmount)}</td>
                      <td><strong>{formatCurrency(detail.totalSalary)}</strong></td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
            <div style={modalStyles.footer}>
              <button className="btn btn-secondary" onClick={() => setShowSalaryModal(false)}>
                Close
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

const modalStyles = {
  overlay: {
    position: 'fixed',
    top: 0,
    left: 0,
    right: 0,
    bottom: 0,
    backgroundColor: 'rgba(0, 0, 0, 0.5)',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    zIndex: 1000,
  },
  modal: {
    backgroundColor: 'white',
    borderRadius: '8px',
    width: '95%',
    maxWidth: '800px',
    maxHeight: '90vh',
    overflow: 'auto',
  },
  header: {
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
    padding: '20px',
    borderBottom: '1px solid #e9ecef',
  },
  closeButton: {
    background: 'none',
    border: 'none',
    fontSize: '24px',
    cursor: 'pointer',
    padding: '0',
    width: '30px',
    height: '30px',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
  },
  footer: {
    padding: '20px',
    borderTop: '1px solid #e9ecef',
    display: 'flex',
    justifyContent: 'flex-end',
  },
};

export default Employees;
