import React, { useState } from 'react';
import AdminLogin from './AdminLogin';
import EmployeeLogin from './EmployeeLogin';
import AdminSignup from './AdminSignup';

const AuthPage = () => {
  const [currentView, setCurrentView] = useState('adminLogin'); // adminLogin, employeeLogin, adminSignup

  const renderCurrentView = () => {
    switch (currentView) {
      case 'adminLogin':
        return (
          <AdminLogin
            onSwitchToEmployee={() => setCurrentView('employeeLogin')}
            onSwitchToSignup={() => setCurrentView('adminSignup')}
          />
        );
      case 'employeeLogin':
        return (
          <EmployeeLogin
            onSwitchToAdmin={() => setCurrentView('adminLogin')}
          />
        );
      case 'adminSignup':
        return (
          <AdminSignup
            onSwitchToLogin={() => setCurrentView('adminLogin')}
          />
        );
      default:
        return (
          <AdminLogin
            onSwitchToEmployee={() => setCurrentView('employeeLogin')}
            onSwitchToSignup={() => setCurrentView('adminSignup')}
          />
        );
    }
  };

  return renderCurrentView();
};

export default AuthPage;
